import { IntegrationForm } from './integration-form';
import { AirtableIntegration } from './integrations/airtable';
import { NotionIntegration } from './integrations/notion';
import { HubspotIntegration } from './integrations/hubspot';

const integrations = [AirtableIntegration, NotionIntegration, HubspotIntegration];

function App() {
  return (
    <div>
      {/* Pasa las integraciones como prop al formulario */}
      <IntegrationForm integrations={integrations} />
    </div>

  ); const [hubspotItems, setHubspotItems] = useState([]);

    // Llamar esta función después de la autenticación exitosa
    const loadHubspotItems = async (user, org) => {
        try {
            const response = await fetch(
                `http://localhost:8000/integrations/hubspot/items?user_id=${user}&org_id=${org}`
            );
            const data = await response.json();
            setHubspotItems(data);
        } catch (error) {
            console.error("Error loading items:", error);
        }
    };

    return (
        <div>
            <h2>Contactos de HubSpot</h2>
            <div className="items-grid">
                {hubspotItems.map((item) => (
                    <div key={item.id} className="item-card">
                        <h3>{item.name}</h3>
                        <div className="item-details">
                            <p>ID: {item.id}</p>
                            <p>Tipo: {item.type}</p>
                            <p>Compañía: {item.parent_path_or_name}</p>
                            <p>Creado: {item.creation_time}</p>
                            <a href={item.url} target="_blank" rel="noreferrer">
                                Ver en HubSpot
                            </a>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
