import { useState } from 'react';
import {
    Box,
    Autocomplete,
    TextField,
} from '@mui/material';
import { NotionIntegration, NotionConfig } from './integrations/notion';
import { AirtableIntegration, AirtableConfig } from './integrations/airtable';
import { HubspotIntegration, HubspotConfig } from './integrations/hubspot';
import { DataForm } from './data-form';

// Mapeo de nombres de integración a sus componentes
const integrationMapping = {
  [NotionConfig.name]: NotionIntegration,
  [AirtableConfig.name]: AirtableIntegration,
  [HubspotConfig.name]: HubspotIntegration,
};

// Opciones para el Autocomplete
const integrationOptions = [
  NotionConfig.name,
  AirtableConfig.name,
  HubspotConfig.name
];

export const IntegrationForm = () => {
    const [integrationParams, setIntegrationParams] = useState({});
    const [user, setUser] = useState('TestUser');
    const [org, setOrg] = useState('TestOrg');
    const [currType, setCurrType] = useState(null);

    // Componente de integración actual
    const CurrIntegration = integrationMapping[currType];

    return (
        <Box display='flex' justifyContent='center' alignItems='center' flexDirection='column' sx={{ width: '100%' }}>
            <Box display='flex' flexDirection='column'>
                <TextField
                    label="User"
                    value={user}
                    onChange={(e) => setUser(e.target.value)}
                    sx={{ mt: 2 }}
                />
                <TextField
                    label="Organization"
                    value={org}
                    onChange={(e) => setOrg(e.target.value)}
                    sx={{ mt: 2 }}
                />
                <Autocomplete
                    id="integration-type"
                    options={integrationOptions}
                    sx={{ width: 300, mt: 2 }}
                    renderInput={(params) => <TextField {...params} label="Integration Type" />}
                    onChange={(e, value) => setCurrType(value)}
                />
            </Box>

            {currType && CurrIntegration && (
                <Box sx={{ mt: 2 }}>
                    <CurrIntegration 
                        user={user} 
                        org={org} 
                        setIntegrationParams={setIntegrationParams}
                    />
                </Box>
            )}

            {integrationParams?.credentials && (
                <Box sx={{ mt: 2 }}>
                    <DataForm 
                        integrationType={currType}
                        credentials={integrationParams?.credentials} 
                    />
                </Box>
            )}
        </Box>
    );
}// En tu componente principal (App.jsx o similar)

function App() {
  const [hubspotItems, setHubspotItems] = useState([]);

  // Llamar esta función después de autenticar
  const loadHubspotData = async (user, org) => {
    try {
      const response = await fetch(
        `http://localhost:8000/integrations/hubspot/items?user_id=${user}&org_id=${org}`
      );

      if (!response.ok) throw new Error('Error cargando datos');

      const data = await response.json();
      setHubspotItems(data);
      console.log("Datos de HubSpot:", data);

    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      {/* Botón de conexión */}
      <HubspotConnectButton onConnect={loadHubspotData} />

      {/* Listado de items */}
      {hubspotItems.map(item => (
        <div key={item.id} className="contact-card">
          <h3>{item.name}</h3>
          <p>ID: {item.id}</p>
          <p>Empresa ID: {item.parent_id}</p>
          <p>Creado: {new Date(item.creation_time).toLocaleDateString()}</p>
          <a href={item.url} target="_blank" rel="noopener noreferrer">
            Ver en HubSpot
          </a>
        </div>
      ))}
    </div>
  );
}
