import { useState, useEffect } from 'react';
import { IntegrationForm } from './integration-form';
import { AirtableIntegration } from './integrations/airtable';
import { NotionIntegration } from './integrations/notion';
import { HubspotIntegration } from './integrations/hubspot';

const integrations = [AirtableIntegration, NotionIntegration, HubspotIntegration];

function App() {
  const [hubspotItems, setHubspotItems] = useState([]);

  // Función para cargar y mostrar los contactos
  const loadHubspotItems = async (user, org) => {
    try {
      const response = await fetch(
        `http://localhost:8000/integrations/hubspot/items?user_id=${user}&org_id=${org}`
      );
      const data = await response.json();
      setHubspotItems(data);
      console.log('Contactos obtenidos de HubSpot:', data);
    } catch (error) {
      console.error('Error cargando contactos:', error);
    }
  };

  return (
    <div>
      <IntegrationForm 
        integrations={integrations} 
        onConnectSuccess={loadHubspotItems} 
      />

      {/* Sección para mostrar resultados */}
      <div style={{ marginTop: '2rem' }}>
        <h3>Contactos de HubSpot:</h3>
        <pre>{JSON.stringify(hubspotItems, null, 2)}</pre>
      </div>
    </div>
  );
}

export default App;
