import { useState } from 'react';
import {
    Box,
    Button,
    CircularProgress
} from '@mui/material';
import axios from 'axios';

// frontend/src/integrations/hubspot.js
export const HubspotIntegration = ({ user, org, setIntegrationParams }) => {
    const [isConnected, setIsConnected] = useState(false);
    const [isConnecting, setIsConnecting] = useState(false);

    const handleConnect = async () => {
        try {
            setIsConnecting(true);
            
            // 1. Obtener URL de autorización (GET con query params)
            const authResponse = await fetch(
                `http://localhost:8000/integrations/hubspot/authorize?user_id=${user}&org_id=${org}`,
                { method: 'GET' }
            );

            if (!authResponse.ok) {
                const errorData = await authResponse.json();
                throw new Error(errorData.detail || 'Error al obtener URL de autorización');
            }

            const { url } = await authResponse.json();
            const authWindow = window.open(url, 'HubSpot Auth', 'width=600,height=600');

            // 2. Verificar credenciales después de cerrar ventana
            const checkCredentials = async () => {
                try {
                    const credentialsResponse = await fetch(
                        `http://localhost:8000/integrations/hubspot/credentials?user_id=${user}&org_id=${org}`
                    );
                    
                    if (credentialsResponse.ok) {
                        const credentials = await credentialsResponse.json();
                        setIntegrationParams({
                            credentials: credentials.access_token,
                            type: 'HubSpot'
                        });
                        setIsConnected(true);
                    }
                } catch (error) {
                    console.error('Error verificando credenciales:', error);
                } finally {
                    setIsConnecting(false);
                }
            };

            const interval = setInterval(() => {
                if (authWindow.closed) {
                    clearInterval(interval);
                    checkCredentials();
                }
            }, 500);

        } catch (error) {
            console.error('Error de conexión:', error);
            setIsConnecting(false);
        }
    };

    return (
        <Box sx={{ mt: 2 }}>
            <Box display="flex" alignItems="center" justifyContent="center">
                <Button
                    variant="contained"
                    onClick={handleConnect}
                    disabled={isConnecting || isConnected}
                    color={isConnected ? 'success' : 'primary'}
                    startIcon={isConnecting && <CircularProgress size={20} />}
                >
                    {isConnected ? 'Conectado a HubSpot' : 'Conectar con HubSpot'}
                </Button>
            </Box>
        </Box>
    );
};
// Configuración para el formulario
export const HubspotConfig = {
    name: 'HubSpot',
    slug: 'hubspot',
    authorizeUrl: '/integrations/hubspot/authorize'
};
